mapper.select:
select tod.id, tdf.income, tod.total_price, tod.create_time, tod.receive_time, tod.finish_time, tod.state, tp.name as stateStr, tod.order_num, tod.name, tod.address, tod.room_num, tod.longitude, tod.latitude, tod.tel, tod.deliveryman_id, td.name as deliverymanName, tod.user_id , tu.name as userName, tod.shop_id , ts.name as shopName, tod.pay_type, tod.coupon_id , tc.name as couponName from t_order_detail tod left join t_deliveryman td on td.id=tod.deliveryman_id left join t_user tu on tod.user_id = tu.id left join t_shop ts on tod.shop_id = ts.id left join t_coupon tc on tod.coupon_id = tc.id left join t_deliveryman_fund tdf on tdf.order_detail_id=tod.id left join t_property tp on tod.state = tp.value and tp.type='order_state'
and tu.name like concat('%',#{userName},'%')
and tod.user_id =#{userId}
and tod.id =#{id}
and tod.state =#{state}
and td.name like concat('%',#{deliverymanName},'%')
and td.id =#{deliverymanId}
and ts.name like concat('%',#{shopName},'%')
and tod.name like concat('%',#{receiptName},'%')
and date_format(tod.create_time,'%Y-%m-%d')>=#{startTime}
and date_format(tod.create_time,'%Y-%m-%d')<=#{endTime}
limit #{page},#{limit}

    select count(*) co from  t_order_detail  tod left join t_deliveryman td on td.id=tod.deliveryman_id
left join t_user tu on tod.user_id = tu.id
left join t_shop ts on tod.shop_id = ts.id
left join t_coupon tc on tod.coupon_id = tc.id
left join t_property tp on tod.state = tp.value and tp.type='order_state'



            and tu.name like concat('%',#{userName},'%')



            and tod.id =#{id}



            and td.id =#{deliverymanId}




            and tu.user_id =#{user_id}



            and tod.state =#{state}



            and tu.name like concat('%',#{deliverymanName},'%')



            and tu.name like concat('%',#{shopName},'%')




            and date_format(tod.create_time,'%Y-%m-%d')>=#{startTime}


            and date_format(tod.create_time,'%Y-%m-%d')<=#{endTime}

select tod.id, tod.total_price, tod.create_time, tod.receive_time, tod.finish_time, tod.state, tp.name as stateStr, tod.order_num, tod.name, tod.address, tod.room_num, tod.longitude, tod.latitude, tod.tel, tod.deliveryman_id, td.name as deliverymanName, tod.user_id , tu.name as userName, tod.shop_id , ts.name as shopName, tod.pay_type, tod.coupon_id , tc.name as couponName from t_order_detail tod left join t_deliveryman td on td.id=tod.deliveryman_id left join t_user tu on tod.user_id = tu.id left join t_shop ts on tod.shop_id = ts.id left join t_coupon tc on tod.coupon_id = tc.id left join t_property tp on tod.state = tp.value and tp.type='order_state'
and td.id =#{deliverymanId}
and tod.state=3
